import re

# Задание-1:
# Вывести символы в нижнем регистре, которые находятся вокруг
# 1 или более символов в верхнем регистре.
# Т.е. из строки "mtMmEZUOmcq" нужно получить ['mt', 'm', 'mcq']
# Решить задачу двумя способами: с помощью re и без.


# Задание-2:
# Вывести символы в верхнем регистре, слева от которых находятся
# два символа в нижнем регистре, а справа - два символа в верхнем регистре.
# Т.е. из строки 
# "GAMkgAYEOmHBSQsSUHKvSfbmxULaysmNOGIPHpEMujalpPLNzRWXfwHQqwksrFeipEUlTLec"
# нужно получить список строк: ['AY', 'NOGI', 'P']
# Решить задачу двумя способами: с помощью re и без.


def first_with_re(line):
  new_line = re.split('[A-Z]', line)
  new_line = [i for i in new_line if i != '']

  return new_line

def second_with_re(line):
  new_line = re.findall(r'[a-z]{2}[A-Z]+[A-Z]{2}', line)
  l = [re.sub('[a-z]{2}', '', i) for i in new_line]
  l2 = [re.sub('[A-Z]{2}$', '', i) for i in l]
  return l2


if __name__ == "__main__":

  line = ''

  print(first_with_re(line))

  line_2 = ''

  print(second_with_re(line_2))
  
